import { EventSourcePolyfill } from 'event-source-polyfill';
import authHeader from '../service/AuthHeader';
import React, { useEffect, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';

function EventListner() {
   
    const [listening, setListening] = useState(false);
    const [data, setData] = useState([]);
    let eventSource = undefined;

    useEffect(() => {
        if (!listening) {
            var EventSource = EventSourcePolyfill; 
            eventSource = new EventSource("http://localhost:8080/api/inventory/manager/subscribe", { headers: authHeader() });

            eventSource.onopen = (event) => {
                console.log("connection opened")
            }

            eventSource.onmessage = (event) => {
                console.log("result", event.data);
                toast(event.data, { position: "bottom-right", hideProgressBar: true });
               // setData(old => [...old, event.data])
            }
            //Amortized
            eventSource.addEventListener("amortized", (event) => {
                console.log("show data = "+(JSON.stringify(event.data)))
            })

            eventSource.onerror = (event) => {
                console.log(event.target.readyState)
                if (event.target.readyState === EventSource.CLOSED) {
                    console.log('eventsource closed (' + event.target.readyState + ')')
                }
                eventSource.close();
            }

            setListening(true);
        }

        return () => {
            eventSource.close();
            console.log("eventsource closed")
        }

    }, [])

    return (
        <div className="">
            
        </div>
    );

    {/* <div >
          <AppInstructor/>
      </div>*/}
    //);
}

export default EventListner;
