import React, { Component } from 'react';
import DeliveryDetailDataService from '../service/DeliveryDetailDataService';
import DeliveryDataService from '../service/DeliveryDataService';
//import ProductDetailDataService from '../service/ProductDetailDataService';
import '../myStyles/Style.css';
import DatePicker from "react-datepicker";
import CustomSelect from './Filters/CustomSelect';
import InventoryNumberInnerComponent from './InventoryNumberInnerComponent'

class DDInnerComponent extends Component {
    constructor(props) {
        super(props)

        this.state =
            {
            ddUpdateShow: props.ddUpdateShow ,
                items: props.items,
                products: props.products,
            ddmessage: props.ddmessage,
            producterror: null

            }
    }

    updatePdChildClicked = (pd, x, y, i) => {
        let pdmessages = this.state.pdmessage;
        if (pdmessages[x] == null) pdmessages[x] = []
        if (pdmessages[x] != null && pdmessages[x][y] != null)
            pdmessages[x][y] = null;

        let show = this.state.pdUpdateShow;
        show.show = !show.show;
        if (show.show == true) {
            show.pd = JSON.parse(JSON.stringify(pd))
            show.x = x;
            show.y = y;
            show.i = i
        } else {
            show.error = ''
        }
        this.setState({
            pdUpdateShow: show,
            pdmessage: pdmessages
        });
    }

    saveUpdateddd = () => {
        let show = this.state.ddUpdateShow;
        if (this.state.producterror != null) {            
            return
        }
        let x = this.state.ddUpdateShow.x;
        let y = this.state.ddUpdateShow.y;
        let items = this.state.items;
        let item = items[x].deliveryDetails[y];

        
        let productId = (show.dd && show.dd.productId)||null;
        let price = (show.dd && show.dd.pricePerOne)||null;

        // console.log("date.toISOString() = " + date.toISOString());
        // console.log("date = " + date);
        // console.log("new Date(item.date) = " + new Date(item.date));
        //console.log("new Date(item.date) === date " +(new Date(item.date) === date ));

        if (!price || !productId) {
            show.error = "required fields can't be empty !!!";
            this.setState({ ddUpdateShow: show })
        } else if (item && productId == item.productId && price == item.pricePerOne) {
            show.error = "delivery detail hasn't changed";
            this.setState({ ddUpdateShow: show })
        } else {
            console.log("all good");
            let item = show.dd;
            item.deliveryId = items[x].id;
            console.log("in submit delivery d item = " + JSON.stringify(item));
            DeliveryDetailDataService.save(item)
                .then((response) => {
                    console.log("delivery d submit response = " + response.data);
                    //item.supplierName = (this.state.suppliers.filter(s => s.value == item.supplierId))[0].name;
                    item.id = item.id || response.data;
                    y = (y || items[x].deliveryDetails.length );
                    
                    console.log("y = " + y);
                    items[x].deliveryDetails[y] = item;

                    let total = 0;
                    items[x].deliveryDetails.map(i => total = total + (i.pricePerOne * i.quantity));
                    items[x].total = items[x].deliveryDetails.map(i => i.pricePerOne * i.quantity)
                   
                    //message = "update successful"
                    this.props.setItems(items);
                    this.props.updateChildClicked(null);
                    let msg = this.state.ddmessage;
                    msg[x] = "update successful";
                    this.props.setddMessage(msg);
                    //this.props.setdeliveryUpdateShow(null)


                    //let path = this.state.deliveryView.length > 0 ? '/deliveries?deliveryView=DeliveryDetailView' : '/deliveries';
                    // this.props.history.push(path)
                }).catch((error) => {
                    console.log("delivery submit error= " + error.data);
                    show.error = "errors occured : " + error + " !!!";
                    this.setState({ ddUpdateShow: show })
                })
        }
    }

    isProductPresent = (value) => {
        let isPresent = false;

        let x = this.state.ddUpdateShow.x;
        let y = this.state.ddUpdateShow.y;
        let items = this.state.items;        
        let dds = items[x].deliveryDetails;

        dds.map((dd, i) => {
            if ((y != null && y != i && dd.productId == value) || (y == null && dd.productId == value) )
                isPresent = true
        });

        return isPresent
    }

    onProductChange = (selected) => {
               
        let ddUpdateShow = this.state.ddUpdateShow;
        if (ddUpdateShow.dd == null) ddUpdateShow.dd = {}
        ddUpdateShow.dd.productId = selected.value;
        ddUpdateShow.dd.productName = selected.label;
        this.setState({
            ddUpdateShow: ddUpdateShow,
            producterror: this.isProductPresent(selected.value) ? 'product already exist in this delivery !!!' : null
        })

    }

    render() {
        return (
            <>
                {this.state.pdUpdateShow && this.state.pdUpdateShow.show == true &&
                    <InventoryNumberInnerComponent
                        pdUpdateShow={this.state.pdUpdateShow}
                        pdShow={this.state.pdShow}
                        pdmessage={this.state.pdmessage}
                        updatePdChildClicked={() => this.updatePdChildClicked(null)}
                        setpdShow={(value) => this.setState({ pdShow: value })}
                        setpdMessage={(value) => this.setState({ pdmessage: value })}
                    //setpdUpdateShow={(value) => this.setState({ pdUpdateShow: value })} 
                    />}
                <div className={this.state.ddUpdateShow.show ? "overlay d-block" : "d-none"}></div>
                <div className={this.state.ddUpdateShow.show ? "modal d-block" : "d-none"}
                    style={{ width: !this.state.ddUpdateShow.dd || !this.state.ddUpdateShow.dd.id ? "85%" : "50%" , height: "62%" }}>
                    <span class="close" onClick={() => this.props.updateChildClicked(null)}>&times;</span>
                    <h2>{this.state.ddUpdateShow.dd ? "update" : "add"} delivery detail </h2>
                    {this.state.ddUpdateShow.error && this.state.ddUpdateShow.error.length > 1 &&
                        <div className="alert alert-warning d-flex">{this.state.ddUpdateShow.error}
                            <i class="fa fa-close ml-auto pr-3 pt-1"
                                onClick={() => {
                                    let show = this.state.ddUpdateShow;
                                    show.error = '';
                                    this.setState({ ddUpdateShow: show })
                                }}>
                            </i>
                        </div>}
                    <div className="inline w-50">
                    <h6 className={this.state.ddUpdateShow.error && this.state.ddUpdateShow.error.length > 1 ?
                        "ml-5" : "mt-5 ml-5"}>product : </h6>
                    <CustomSelect
                        className={this.state.producterror ? "mb-0" : ""}
                        items={this.state.products}
                        value={(this.state.ddUpdateShow.dd && this.state.ddUpdateShow.dd.productId)}
                        
                        onChange={(selected) => this.onProductChange(selected)}
                    />
                    {this.state.producterror &&
                        <div className="alert alert-warning d-flex in-error">{this.state.producterror}
                            <i class="fa fa-close ml-auto pr-3 pt-1"
                            onClick={() => {
                                let err = this.state.producterror;
                                err = null;
                                this.setState({ producterror: err })
                                }}>
                            </i>
                        </div>}
                    <h6 className="ml-5">price per one :</h6>
                    <input type="number" value={this.state.ddUpdateShow.dd ? this.state.ddUpdateShow.dd.pricePerOne : ''} min="0" onChange={(value) => {
                        let show = this.state.ddUpdateShow;
                        if (show.dd == null) show.dd = {}
                        show.dd.pricePerOne = value.target.value;
                        this.setState({ ddUpdateShow: show })
                    }} />

                   
                   
                    
                    </div>
                
                    <div className="inline w-50">
                        <h6 className="inline">inventories</h6>
                        <button className="btn btn-mybtn px-5 ml-5" onClick={() => { }}>add</button>
                    </div>

                    <div>
                        <div className="inline w-50">
                        <button className="btn btn-mybtn px-5 ml-5" onClick={this.saveUpdateddd}>Save</button>
                            <button className="btn btn-mybtn btn-delete px-5 ml-3" onClick={() => this.props.updateChildClicked(null)}>Cancel</button>
                        </div>
                        <div className="inline w-50">
                        {
                            (!this.state.ddUpdateShow.dd || !this.state.ddUpdateShow.dd.id) &&
                                <button className="btn btn-mybtn px-5 ml-3" onClick={() => { this.updatePdChildClicked({}, x, y, i)}}>Add inventory&nbsp;<i class="fa fa-angle-double-right" aria-hidden="true"></i></button>

                            }
                        </div>
                    </div>
                </div>
            </>
        )
    }

}
export default DDInnerComponent
