import React, { Component } from 'react';
import Select from 'react-select';
//import { useHistory } from "react-router-dom"


const options = [
    { value: '5', label: '5' },
    { value: '10', label: '10' },
    { value: '20', label: '20' },
    { value: '50', label: '50' },
];

class PaginationComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            
        }

        this.onPageClicked = this.onPageClicked.bind(this)
        this.onCountChange = this.onCountChange.bind(this)

    }

    onCountChange = selectedOption => {
       // this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);

        let path = window.location.pathname;
        console.log('path = ' + path);

        let search = window.location.search;
        console.log('search = ' + search);

        let newPath = ``;

        if (search.length < 1) {

            newPath = path + `?${this.props.prefix}.itemsPerPage=${selectedOption}`;
            console.log('new path = ' + newPath)

        }
        else {
            // newPath += '?';
            //  search.replace("?", "");
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            console.log('search = ' + search);
            let searchItems = search.split('&');

            for (let i = 0; i < searchItems.length; i++) {

                if (searchItems[i].startsWith(this.props.prefix))
                    continue
                else
                    if (i === searchItems.length - 1)
                        newPath += searchItems[i]
                    else
                        newPath += searchItems[i] + '&'

            }
            newPath = path + '?' + newPath + this.props.prefix + '.itemsPerPage=' + selectedOption;

        }

        // this.props.history.push(`${newPath}`)
        console.log('new path = ' + newPath);
        window.location.href = newPath;
    }

    onPageClicked(pageNumber) {

        if(pageNumber < 0)  pageNumber=0;
        console.log('pageNumber = ' + pageNumber);

        let path = window.location.pathname;
        console.log('path = ' + path);

        let search = window.location.search;
        console.log('search = ' + search);

        let newPath = ``;

        if (search.length < 1) {

            newPath = path + `?${this.props.prefix}.page=${pageNumber}`;
            console.log('new path = ' + newPath)

        }
        else {
           // newPath += '?';
          //  search.replace("?", "");
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            console.log('search = ' + search);
            let searchItems = search.split('&');

            for (let i = 0; i < searchItems.length; i++) {

                if (searchItems[i].startsWith(this.props.prefix + '.page'))
                    continue
                else
                    if (i === searchItems.length-1)
                        newPath += searchItems[i]
                    else
                        newPath += searchItems[i]+'&'

            }
            newPath = path + '?' + newPath + this.props.prefix + '.page=' + pageNumber;
           
        }

        // this.props.history.push(`${newPath}`)
        console.log('new path = ' + newPath);
        window.location.href = newPath;

       // let urlElements = window.location.href.split('/');
        //console.log(urlElements);

       
    }



    render() {

     /* private String Prefix ;
        private int Page ;
        private int PagesCount ;
        private int ItemsPerPage ;
        private int ItemsCount ;*/

       const numrows = this.props.pagesCount;
       console.log('pagesCount = ' + numrows);
       /* const tbodyContent = [];

        for (let i = 0; i < numrows; i++) {
            tbodyContent.push(<li class="page-item"><a class="page-link" href="#">i+1</a></li>);
        }*/

        const pageNumbers = [];
       // const location = useLocation()
       // console.log('url = ' + location.pathname);
        for (let i = 1; i <= numrows; i++) {


            pageNumbers.push(<li key={i} class="page-item"><a class="page-link" href="#" onClick={() => this.onPageClicked(i-1)}>{i}</a></li>);
        }

        const { selectedOption } = this.props.itemsPerPage;


        return (
            <nav aria-label="Page navigation example">
               
                <ul class="pagination">
                    <li class="page-item"><a class="page-link" href="#" onClick={() => this.onPageClicked(this.props.page-1)}>Previous</a></li>
                    <ul class="pagination">
                        {
                            pageNumbers
                        }
                        </ul>
                    <li class="page-item"><a class="page-link" href="#" onClick={() => this.onPageClicked(this.props.page + 1)}>Next</a></li>
                </ul>

                <Select
                    value={selectedOption}
                    onChange={this.onCountChange}
                    options={options}
                />

            </nav>
            
            
            )
    }
}

export default PaginationComponent
