import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import CustomSelect from './CustomSelect';
import './Filter.css'

class UserProfileFilter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            all: props.all,            
            userNames: props.userNames,
            userId: props.userId,
            inventoryNumbers: props.inventoryNumbers,
            productDetailId: props.productDetailId,
            prefix: props.prefix
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.resetForm = this.resetForm.bind(this)
    }

    onSubmit(values) {

        let path = window.location.pathname;
        let search = window.location.search;
        let newPath = ``;

        if (search.length > 1) {
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            let searchItems = search.split('&');
            for (let i = 0; i < searchItems.length; i++) {

                if (searchItems[i].startsWith('Pager.itemsPerPage='))
                    newPath += searchItems[i] + '&'
            }
        }

        let prefix = this.state.prefix;
        Object.entries(values).map(([key, value]) => {
            if (!key.endsWith("s") && value && value.length > 1) {
                newPath += prefix + '.' + key + '=' + value + '&'
            }

        })
        newPath = newPath.substring(0, newPath.length - 1);
        newPath = path + '?' + newPath;
        console.log('newPath =' + newPath);

        window.location.href = newPath;
    }

    resetForm() {

        window.location.href = window.location.pathname;
       /* this.setState({
            all: '',
            firstName: '',
            lastName: '',
            userName: '',
            email: '',
        });
        console.log('in reset form ');*/
    }

    render() {

        let { all, userNames, userId, inventoryNumbers, productDetailId } = this.state
        return (

            <Formik
                initialValues={{ all, userNames, userId, inventoryNumbers, productDetailId }}
                onSubmit={this.onSubmit}
                enableReinitialize={true}
            >
                {({ props, setFieldValue, values }) => (
                    <Form className="filter-form">
                        <fieldset >
                            <div className="inline">
                                <label>user&nbsp;</label>
                                <CustomSelect
                                    className={"inline inline-2-5"}
                                    items={userNames}
                                    value={values.userId}
                                    onChange={(selected) => setFieldValue("userId", selected.value)}
                                />
                            </div>
                            <div className="inline">
                                <label >inventory number&nbsp;</label>
                                <CustomSelect
                                    className={"inline inline-2-5"}
                                    items={inventoryNumbers}
                                    value={values.productDetailId}
                                    onChange={(selected) => setFieldValue("productDetailId", selected.value)}
                                />
                            </div>
                           
                            <div className="inline">
                                <button className="button" type="submit">Search</button>
                                <button className="button btn-delete" type="button" onClick={this.resetForm}>reset</button>
                            </div>
                        </fieldset>
                    </Form>
                )
                }
            </Formik>
        )
    }
}

export default UserProfileFilter
