import React, { Component } from 'react';
import '../myStyles/Style.css';
import DatePicker from "react-datepicker";
import CustomSelect from './Filters/CustomSelect';
import UserProfileDataService from '../service/UserProfileDataService';
import { Formik, Form, Field, FieldArray, ErrorMessage } from 'formik';


class TimelineInnerComponent extends Component {
    constructor(props) {
        super(props)

        this.state =
            {
                items: [],      
            filter: props.filter, 
            first: '',
            last: '',
            count:''
            
            }
        this.refresh = this.refresh.bind(this)
    }

   
    componentDidMount() {
      
        let search = `?productDetailId=` + this.state.filter.productDetailId + `&givenAfter=` + this.state.filter.givenAfter;
        search += this.state.returnedBefore ? `&returnedBefore=` + this.state.filter.returnedBefore : ``;
        this.refresh(search)
    } 

    refresh(search) {
        UserProfileDataService.retrieveTimeline(search)
            .then(
            response => { 
                console.log("response = " + JSON.stringify(response));
                    
                    this.setState({
                        items: response.data.items || [],
                        first: response.data.first,
                        last: response.data.last,
                        count: response.data.count
                       // pager: response.data.pager,
                    });
                }
            ).catch((error) => {
                this.setState({
                    errormsg: '' + error == 'Error: Request failed with status code 401' ? 'need to login again !!!' : 
                        error.response && typeof error.response.data == 'string' ? error.response.data :
                            error.response.data.message ? error.response.data.message : error
                })
            })
    }

    getFilteredInventoty() {//original
        let filteredInventory = (this.state.filter.productDetailId) ? this.state.filter.inventoryNumbers.find(n => n.value == this.state.filter.productDetailId).name
            : null;
        return filteredInventory
    }

    onSubmit(values) {

        //console.log("in submit delivery");
        //console.log("in submit delivery values.date = " + values.date);//JSON.stringify(values));
        let item = {
            id: this.state.id,
            date: values.date,//.toISOString(),
            number: values.number,
            supplierId: values.supplierId,
            deliveryDetailEditVMs: values.deliveryDetailEditVMs,
            deletedDetailsIds: values.deleteddds,
            targetDate: values.targetDate
        };
        console.log("in submit delivery item = " + JSON.stringify(item));
        DeliveryDataService.save(item)
            .then((response) => {
                console.log("delivery submit response = " + response.data);
                let path = this.state.deliveryView.length > 0 ? '/deliveries?deliveryView=DeliveryDetailView' : '/deliveries';
                this.props.history.push(path)
            }).catch((error) => {
                // preventDefault();
                console.log("error.response.data= " + error.response.data);
                console.log("delivery submit error= " + error);
                console.log("delivery submit error= " + JSON.stringify(error));
                /*this.setState({
                    numErrors: error.response.data //arr
                });*/
                values.numErrors = error.response.data;
                //console.log("after error submit delivery values.supllier.id = " + values.supplierId);//JSON.stringify(values));
            })
        //console.log("after submit delivery values.supllier.id = " + values.supplierId);//JSON.stringify(values));
    }

    validate(values) {
        console.log("validating : values.supplierId = " + values.supplierId);
        let errors = {}
        if (!values.date) {
            errors.date = 'date required field !!!'
        }
        if (this.state.id < 0 && (!values.supplierId || values.supplierId == 'undefined')) {
            errors.supplierId = 'required field !!!'
        }
        if (values.deliveryDetailEditVMs.length < 1) {
            errors.deliveryDetailEditVMs = 'delivery must have products !!!'
        }
        console.log("in validate delivery values errors = " + JSON.stringify(errors));
        return errors
    }
   

    render() {

        let { items, first, last, count } = this.state;
        return (
                <Formik
                    initialValues={{items, first, last, count}}
                    onSubmit={this.onSubmit}
                    validateOnChange={true}
                    validateOnBlur={false}
                    validate={this.validate}
                    enableReinitialize={true}
                >
                    {({ setFieldValue, values }) => (                       
                            <Form>
                                <div className="pt-2 px-2 mx-3 d-flex flex-wrap ">
                                    <div >
                                        <button className="button btn-mybtn" style={{ padding: ".3rem 1.8rem .4rem 1.8rem" }} type="submit">Save changes</button>
                                        <button className="button btn-delete" style={{ padding: ".3rem 1.8rem .4rem 1.8rem" }} onClick={() => { }
                                        }>Cancel</button>
                                    </div>
                                    <div className=" d-inline-flex justify-content-end flex-grow-1 mr-5">
                                        <label className="pager-label mr-3">showing&nbsp;{1}-{this.state.count}&nbsp; of &nbsp; {this.state.count}</label>
                                        <label className="pager-label mr-3">for &nbsp;{this.getFilteredInventoty()}</label></div>
                                </div>
                                <table className="table border-bottom my-table mt-2">
                                    <thead>
                                        <tr>
                                            <th style={{ width: "30px" }}></th>
                                            <th className="w30">user</th>
                                            <th className="wms">given at</th>
                                            <th className="wms">returned at</th>
                                            <th></th>
                                        </tr>
                                    </thead></table>
                            {
                                values.items &&
                                values.items.map((item, i) =>
                                <fieldset key={item.id || i} className="d-flex align-items-top pb-1 pt-1">
                                        <div className="inline" style={{ width: "30px" }}>{i + 1}-</div>
                                        <div className="inline w30">
                                            {/* <label>user&nbsp;</label>*/}
                                            <CustomSelect
                                                className={"inline inline-3"}
                                                items={this.state.filter.userNames}
                                                value={values.items[i].userId}
                                                onChange={(selected) => setFieldValue(`items.${i}.userId`, selected.value)}
                                        />                                        
                                        </div>
                                        <div className="inline wms">
                                            {/*<label className="mb-1">given at&nbsp;</label>*/}
                                            <DatePicker className="form-control inline-2"
                                                dateFormat="dd MMMM yyyy"
                                                locale="en-GB"
                                               // maxDate={values.dateCreatedBefore}
                                                selected={new Date(values.items[i].givenAt)}
                                            onChange={date => setFieldValue(`items.${i}.givenAt`, date)} />
                                        <ErrorMessage name={`items.${i}.givenAt`} component="div"
                                            className="alert alert-warning mbt-01" />
                                        </div>
                                        <div className="inline wms">
                                            {/* <label className="mb-1">returned at&nbsp;</label>*/}
                                            <DatePicker className="form-control inline-2"
                                                dateFormat="dd MMMM yyyy"
                                                locale="en-GB"
                                                // maxDate={values.dateCreatedBefore}
                                                selected={new Date(values.items[i].returnedAt)}
                                            onChange={date => setFieldValue(`items.${i}.returnedAt`, date)} />
                                        <ErrorMessage name={`items.${i}.returnedAt`} component="div"
                                            className="alert alert-warning mbt-01" />
                                        </div>
                                        <div className="inline"></div>
                                        

                                    </fieldset>
                                    )} 
                          
                        </Form>
                    )}
                </Formik>
           
        )
    }
}

export default TimelineInnerComponent
