import React, { Component } from 'react';
import DeliveryDataService from '.../service/DeliveryDataService';
import PaginationComponent from '../PaginationComponent';
import DeliveryFilter from '../Filters/DeliveryFilter';
import '../myStyles/Style.css';
import { CSVLink } from "react-csv";

const headers = [
    { label: "number", key: "number" },
    { label: "date", key: "date" },
    { label: "supplier", key: "supplier.name" },
    { label: "product", key: "deliveryDetails.productName" },
    { label: "quantity", key: "deliveryDetails.quantity" },
    { label: "unit price", key: "deliveryDetails.pricePerOne" }
];

class ListDeliveriesComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [],
            message: null,
            pager: null,
            filter: null,
            search: window.location.search || '',
            alldata: [],
        }
        this.refresh = this.refresh.bind(this)
        this.deleteClicked = this.deleteClicked.bind(this)
        this.deleteChildClicked = this.deleteChildClicked.bind(this)
        this.updateClicked = this.updateClicked.bind(this)
        this.addClicked = this.addClicked.bind(this)
        this.csvLink = React.createRef();
    }

    componentDidMount() {
        this.refresh();
    }

    refresh() {
       DeliveryDataService.retrieveAll(this.state.search)
            .then(
                response => {
                    this.setState({
                        items: response.data.items,
                        pager: response.data.pager,
                        filter: response.data.filter,
                        deliveryView : 'deliveryView'
                    });
                }
            )
    }

    downloadReport = () => {
        let newSearch = this.getSearchAll();
        DeliveryDataService.retrieveAll(newSearch)
            .then(response => {
                this.setState({ alldata: response.data.items });
                this.csvLink.current.link.click()
            })
    }

    getSearchAll = () => {
        let search = this.state.search;
        search.replace(/\s+/g, '');
        let newSearch = '';
        let searchItems = search.split('&');
        for (let i = 0; i < searchItems.length; i++) {
            if (searchItems[i].length < 1 || searchItems[i].startsWith('Pager.itemsPerPage'))
                continue
            else
                newSearch += searchItems[i] + '&'
        }
        newSearch = '?' + newSearch;
        if (newSearch.length > 1) newSearch += '&'
        newSearch += 'Pager.itemsPerPage=2147483647';
        return newSearch;
    }

    deleteClicked(id) {
        DeliveryDataService.delete(id)
            .then(
                response => {
                    this.setState({ message: `Delete successful` })
                    this.refresh()
                }
            )
    }

    deleteChildClicked(id) {//child id
        DeliveryDataService.deleteChild(id)
            .then(
                response => {
                    this.setState({ message: `Delete successful` })
                    this.refresh()
                }
            )
    }

    updateClicked(id) {
        this.props.history.push(`/deliveries/${id}`)
    }

    addClicked() {
        this.props.history.push(`/deliveries/-1`)
    }

    togglemsgbox = () => {
        this.setState({ message: null })
    }

    render() {
        const data = this.state.items;
        const dataAll = '';
        return (
            <div className="px-3">
                {this.state.filter && <DeliveryFilter {...this.state.filter} />}
                <div className="border">
                    <div className="panel-heading">
                        <h6 className="panel-title p-2 d-inline-flex">
                            <strong> Deliveries</strong>
                        </h6>
                        <label className="mx-1 pull-right mr-5">
                            <input className="mx-1 m-2 mt-3 " type="radio" name="DeliveryView" value="deliveryView"
                                checked={this.state.deliveryView == 'deliveryView'}
                                onClick={(value) => this.state.deliveryView != 'deliveryView' ? this.setState({ deliveryView: 'deliveryView' }) : {}} />Delivery View
                        </label>
                        <label className="mx-1 pull-right mr-5">
                            <input className="mx-1 m-2 mt-3 " type="radio" name="DeliveryView" value="deliveryDetailView"
                                checked={this.state.deliveryView == 'deliveryDetailView'}
                                onClick={(value) => this.state.deliveryView == 'deliveryView' ? this.setState({ deliveryView: 'deliveryDetailView' }) : {}} />Delivery Detail View
                        </label>
                    </div>
                    <div className="p-1">
                        <div className=" pt-3 px-2 mx-3 d-flex flex-wrap">
                            <div>
                                <button className="btn btn-mybtn px-5  " onClick={this.addClicked}>Add New</button>
                                <CSVLink
                                    className="btn btn-mybtn px-3 ml-2"
                                    data={data} headers={headers} filename={"deliveries-page.csv"}
                                    asyncOnClick={true}
                                    onClick={() => {}}
                                >
                                    Download this page
                                </CSVLink>
                                <button className="btn btn-mybtn px-3 ml-2" onClick={this.downloadReport}>Download All</button>
                                <CSVLink
                                    data={this.state.alldata}
                                    filename={"deliveries-all.csv"}
                                    className="hidden"
                                    headers={headers}
                                    ref={this.csvLink}
                                    target="_blank"
                                />
                            </div>
                            {this.state.pager && <PaginationComponent {...this.state.pager} />}
                        </div>
                        {this.state.message && <div className="alert alert-success d-flex">{this.state.message}
                            <i class="fa fa-close ml-auto pr-3 pt-1" onClick={this.togglemsgbox}></i>
                        </div>}
                       
                        {this.state.deliveryView == 'deliveryView' &&                            
                                    this.state.items.map(
                                        item =>
                                            <div className="panel-body">
                                                <table className="table  border table-s">
                                                    <tbody>
                                                        <tr>
                                                            <th className="w-25 pl-3"> Number : {item.number} </th>
                                                            <th className="w-25 pl-3"> Date : {
                                                                new Intl.DateTimeFormat("en-GB", {
                                                                    month: "long",
                                                                    day: "2-digit",
                                                                    year: "numeric",                                                                  
                                                                }).format(new Date(item.date))
                                                            }</th>
                                                            <th className=" w-25"> Supplier : {item.supplier.name}</th>
                                                            <th className="d-flex justify-content-end">
                                                                <button className="btn btn-mybtn mr-1" onClick={() => this.updateClicked(item.id)}>Update</button>
                                                                <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteClicked(item.id)}>Delete</button>
                                                            </th>
                                                         </tr>
                                                         <tr>
                                                         <td colspan="4">
                                                                <table className="table border x-Table">
                                                                <tbody>
                                                                    <tr>
                                                                        <td>Product</td>
                                                                        <td>Quantity</td>
                                                                        <td>Unit Price</td>
                                                                        <td>Total</td>
                                                                            <td style={{ width: '140px' }}></td>
                                                                    </tr>
                                                                        {item.deliveryDetails.map(
                                                                                dd =>
                                                                                    <tr key={dd.id}>
                                                                                        <td>{dd.productName}</td>
                                                                                        <td>{dd.quantity}</td>
                                                                                        <td>{new Intl.NumberFormat("en-GB", {
                                                                                            style: "currency",
                                                                                            currency: "BGN",
                                                                                            maximumFractionDigits: 2
                                                                                        }).format(dd.pricePerOne)}</td>
                                                                                        <td>{new Intl.NumberFormat("en-GB", {
                                                                                            style: "currency",
                                                                                            currency: "BGN",
                                                                                            maximumFractionDigits: 2
                                                                                        }).format(dd.pricePerOne * dd.quantity)}</td>
                                                                                        <td><button className="btn btn-mybtn mr-1" onClick={() => this.updateClicked(item.id)}>Update</button>
                                                                                            <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteChildClicked(dd.id)}>Delete</button></td>
                                                                                    </tr>
                                                                                )                                                           
                                                                         }
                                                                </tbody>
                                                                </table>
                                                                <span class="pull-right" style={{ marginRight: '100px' }}><strong>Total Bill : {new Intl.NumberFormat("en-GB", {
                                                                    style: "currency",
                                                                    currency: "BGN",
                                                                    maximumFractionDigits: 2
                                                                }).format(item.total)} </strong> {}</span>
                                                         </td>
                                                        </tr>
                                                    </tbody>
                                                </table>
                                            </div>
                                            
                                         )
                                   }                           

                        {this.state.deliveryView == 'deliveryDetailView' &&
                        <table className="table border-bottom my-table" >
                            <thead>
                                <tr>
                                    <th scope="col" className="px100">number</th>
                                    <th scope="col" className="ws">date</th>
                                    <th scope="col" className="wms">supplier</th>
                                    <th scope="col">product</th>
                                    <th scope="col" className="wxxs">quantity</th>
                                    <th scope="col" className="wxs">unit price</th>
                                    <th className="wxs">total</th>
                                    <th scope="col"></th>
                                </tr>
                            </thead>                           
                            <tbody>
                                {this.state.items.map(
                                        item => 
                                            <tr>
                                                <td colspan="8" className="p0">
                                                    <table className="table my-table mb-0" >
                                                          <tbody className="bold-border">
                                                            {item.deliveryDetails.map(
                                                                    dd =>
                                                                        <tr scope="row" key={dd.id}>
                                                                            <td className="px100">{item.number}</td>
                                                                            <td className="ws">
                                                                                {new Intl.DateTimeFormat("en-GB", {
                                                                                    month: "long",
                                                                                    day: "2-digit",
                                                                                    year: "numeric",
                                                                                }).format(new Date(item.date))}
                                                                            </td>
                                                                            <td className="wms">{item.supplier.name}</td>
                                                                            <td>{dd.productName}</td>
                                                                            <td className="wxxs">{dd.quantity}</td>
                                                                            <td className="wxs">{new Intl.NumberFormat("en-GB", {
                                                                                style: "currency",
                                                                                currency: "BGN",
                                                                                maximumFractionDigits: 2
                                                                            }).format(dd.pricePerOne)}</td>
                                                                            <td className="wxs">{
                                                                                new Intl.NumberFormat("en-GB", {
                                                                                    style: "currency",
                                                                                    currency: "BGN",
                                                                                    maximumFractionDigits: 2
                                                                                }).format(dd.pricePerOne * dd.quantity)}</td>
                                                                            <td><button className="btn btn-mybtn mr-1" onClick={() => this.updateClicked(item.id)}>Update</button>
                                                                                <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteChildClicked(dd.id)}>Delete</button></td>
                                                                        </tr>
                                                                )}
                                                        </tbody>
                                                    </table>
                                                </td>
                                            </tr>                                      
               )}
                            </tbody>
                        </table>
                        }
                    </div>
                </div>
            </div>
        )
    }
}

export default ListDeliveriesComponent
