import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import DeliveryDataService from '../service/DeliveryDataService';
import '../myStyles/Style.css';
import CustomSelect from './Filters/CustomSelect';
import DatePicker from "react-datepicker";

class DeliveryComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            number: '',
            date: '',
            supplierId: '',
            suppliers: [],
            deliveryDetailEditVMs: [],
            products: [],
            index: '',
            ddEditVMerror:null
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
        this.cancelForm = this.cancelForm.bind(this)
    }

    componentDidMount() {

       DeliveryDataService.retrieve(this.state.id)
            .then(response => {
                console.log('got response in component did mount id = '+this.state.id);
               /* if (this.state.id > 0) {
                    this.setState({
                        supplierId: response.data.supplierId,                                            
                    })
                }*/
                this.setState({
                    supplierId: response.data.supplierId || '',  
                    number: response.data.number, 
                    date: response.data.date,
                    suppliers: response.data.suppliers,  
                    deliveryDetailEditVMs: response.data.deliveryDetailEditVMs || [],
                    products: response.data.products || []
                });
            })
    }

    onSubmit(values) {
        console.log('in onsubmitn');
        let item = {
            id: this.state.id,
            date: values.date,
            number:values.number,
            supplierId: (values.supplierId.length > 1 ? values.supplierId : null),
            deliveryDetailEditVMs: values.deliveryDetailEditVMs,
            targetDate: values.targetDate
        }
        console.log('delivery = ' + JSON.stringify(item));
        DeliveryDataService.save(item)
            .then((response) => {
                console.log('response = ' + JSON.stringify(response.data));
                this.props.history.push('/deliveries')
    })
    }

    validate(values) {
        console.log('in validate');
        let errors = {}
        if (!values.date) {
            errors.date = 'required field !!!'
        }
        console.log('supplierId = ' + values.supplierId);
        console.log('(!values.supplierId)  = ' + (!values.supplierId) );

        if (!values.supplierId) {
            errors.supplierId = 'required field !!!'
        }
        if (values.deliveryDetailEditVMs.length < 1) {
            errors.deliveryDetailEditVMs = 'delivery must have products !!!'
        }
        console.log('errors.supplierId  = ' + errors.supplierId );
        console.log('errors length = ' + errors.length);
        return errors
    }

    cancelForm() {
        this.props.history.push('/deliveries')
    }

    togglemsgbox = () => {
        this.setState({ ddEditVMerror: null })
    }

    render() {
        console.log('rendering');
        let { id, number, date, supplierId, suppliers, deliveryDetailEditVMs, products, index, ddEditVMerror} = this.state ;
        let ddEditVM = { id: '', productId: '', productName: '', quantity: '', pricePerOne: '' };
        //let ddEditVMerror = '';
        return (
            <div className="container">
                {this.state.id > 0 ? <h3 className="mb-3"> Update Delivery</h3> : <h3 className="mb-3"> Add New Delivery </h3>}
                <Formik
                    initialValues={{
                        id, number, date, supplierId, suppliers, deliveryDetailEditVMs, products, index, ddEditVMerror, ddEditVM
                    }}
                    onSubmit={this.onSubmit}
                    validateOnChange={false}
                    validateOnBlur={false}
                    validate={this.validate}
                    enableReinitialize={true}
                >
                    {
                        ({ setFieldValue, values }) => (
                            <Form>
                                <Field className="form-control" type="text" name="id" hidden />
                                <fieldset className="form-group">
                                    <label>number</label>
                                    <Field className="form-control w-25" type="text" name="number" disabled
                                    />                                   
                                </fieldset>
                                <fieldset className="form-group">
                                <fieldset className="form-group d-inline-block">
                                    <label>date</label>
                                    <div>
                                    <DatePicker
                                                className="form-control inline-2-5"
                                        dateFormat="dd MMMM yyyy"
                                        locale="en-GB"                                       
                                        selected={values.date && new Date(values.date)}
                                        onChange={date => setFieldValue("date", date)} />
                                    <ErrorMessage name="date" component="div"
                                            className="alert alert-warning" />
                                    </div>
                                </fieldset>
                                   
                                    <fieldset className="form-group d-inline-block px-5">
                                        <label>supplier</label>
                                        <div>
                                    <CustomSelect
                                        id="supplierId"
                                        name="supplierId"
                                            className={"d-inline-block inline-4"}
                                        items={suppliers}
                                        value={values.supplierId}
                                        onChange={(value) => setFieldValue("supplierId", value.value)}
                                    />
                                           
                                        </div>
                                        <ErrorMessage name=" supplierId" component="div"
                                            className="alert alert-warning" />
                                    </fieldset>
                                </fieldset>

                                <fieldset className="form-group">
                                    <div className="d-inline">
                                        <label>product :&nbsp;</label>
                                        <CustomSelect
                                            className={"d-inline-block inline-2"}
                                            items={products}
                                            value={values.ddEditVM.productId}
                                            onChange={(selected) => {
                                                let ddVM = values.ddEditVM;
                                                ddVM.productId = selected.value;
                                                ddVM.productName = selected.label;
                                                setFieldValue("ddEditVM", ddVM);                                               
                                            }}
                                        />
                                    </div>
                                    <div className="d-inline px-3">
                                        <label className="mb-1">quantity :&nbsp;</label>                                       
                                        <Field className="form-control d-inline p-1 inline-100px" type="number" min="0" name="ddEditVM.quantity" />                                        
                                    </div>
                                    <div className="d-inline px-3">
                                        <label className="mb-1">pricePerOne :&nbsp;</label>
                                        <Field className="form-control d-inline p-1 inline-100px" type="number" min="0" name="ddEditVM.pricePerOne" />
                                    </div>
                                    <button className="btn btn-mybtn mr-1" type="button" onClick={() => {                                       
                                        if (values.index === '') {                                           
                                            let equals = false;
                                            values.deliveryDetailEditVMs.map((x) => {
                                                if (x.productName == values.ddEditVM.productName)
                                                    equals = true                                                  
                                            });                                        
                                            if (!equals) setFieldValue("deliveryDetailEditVMs", deliveryDetailEditVMs.push(values.ddEditVM))
                                            else {
                                                let ddError = values.ddEditVMerror;
                                                ddError = values.ddEditVM.productName + ' already exist\'s in the list!!!';                                               
                                                setFieldValue("ddEditVMerror", 'error');                                               
                                            }
                                        }
                                        else {
                                            let list = values.deliveryDetailEditVMs;
                                            list[values.index] = values.ddEditVM;
                                            setFieldValue("deliveryDetailEditVMs", list);
                                        } 
                                        
                                    }}>{values.index !== '' ? "Update" : "Add"}</button>

                                    <button className="btn btn-mybtn btn-delete" type="button" onClick={() => {
                                        let VM = values.ddEditVM;
                                        VM = { id: '', productId: 'undefined', productName: '', quantity: '', pricePerOne: '' };
                                        setFieldValue("ddEditVM", VM);
                                        setFieldValue("index", '');
                                        setFieldValue("ddEditVMerror", null);
                                    }}>reset</button>                                    
                                    {values.ddEditVMerror &&
                                        <div className="alert alert-warning d-flex">{values.ddEditVMerror}
                                            <i class="fa fa-close ml-auto pr-3 pt-1"
                                                onClick={() => setFieldValue("ddEditVMerror", null)}></i></div>
                                    }   
                                    <ErrorMessage name=" deliveryDetailEditVMs" component="div"
                                        className="alert alert-warning" />
                                </fieldset>
                               
                                    <table className="table border x-Table">
                                        <tbody>
                                            <tr>
                                                <td>Product</td>
                                                <td>Quantity</td>
                                            <td>Unit Price</td>
                                            <td style={{ width: '140px' }}></td>
                                        </tr>
                                    {deliveryDetailEditVMs.map((dd,index) =>
                                                <tr key={index}>
                                                    <td>{dd.productName}</td>
                                                    <td>{dd.quantity}</td>
                                                    <td>{new Intl.NumberFormat("en-GB", {
                                                        style: "currency",
                                                        currency: "BGN",
                                                        maximumFractionDigits: 2
                                                    }).format(dd.pricePerOne)}</td>
                                                    <td><button className="btn btn-mybtn mr-1" type="button" onClick={() => {
                                                        console.log('index = ' + index);
                                                        setFieldValue("index", index);
                                                        setFieldValue("ddEditVM", dd)
                                                    }}>Update</button>
                                                    <button className="btn btn-mybtn btn-delete" type="button"
                                                        onClick={() => deliveryDetailEditVMs.splice(index, 1)}>Delete</button></td>
                                                </tr>
                                    )
                                        }
                                    </tbody>
                                </table>                                                    

                                <button className="btn btn-mybtn px-5" type="submit">Save</button>
                                <button className="btn btn-mybtn btn-delete px-5 ml-5" type="button" onClick={this.cancelForm}>cancel</button>
                            </Form>
                        )
                    }
                </Formik>
            </div>
        )
    }
}

export default DeliveryComponent
