import React, { Component } from 'react';
import '../myStyles/Style.css';
import DatePicker from "react-datepicker";
import CustomSelect from './Filters/CustomSelect';


class DeleteAllInnerComponent extends Component {
    constructor(props) {
        super(props)

        this.state =
            {
                productDetails: props.productDetails
            }
    }


    render() {
        return (
            <>

                <div className="overlay d-block"></div>
                <div className= "modal d-block">
                    {/* <span class="close pt-3" onClick={() => this.props.updateChildClicked(null)}>&times;</span>*/}
                    <h2>Delete <b>All</b> before : </h2>

                    
                        
                            {this.state.error &&
                                <div className="alert alert-warning d-flex">{this.state.error}
                                    <i class="fa fa-close ml-auto pr-3 pt-1"
                                        onClick={() => {
                                            
                                            this.setState({ error: null })
                                        }}>
                                    </i>
                        </div>}


                            <h6 className={this.state.error ?
                                "ml-5" : "mt-5 ml-5"}>inventory : </h6>
                    <CustomSelect
                        className="inline inline-2"
                                items={this.state.productDetails}
                                value={(this.state.productDetailId)}

                            onChange={(selected) => this.setState({productDetailId : selected.value})}
                            />
                           
                           
                            <button className="btn btn-mybtn p-x-5 ml-5" onClick={this.saveUpdateddd}>Save</button>
                            <button className="btn btn-mybtn btn-delete px-5 ml-5" onClick={() => this.props.updateChildClicked(null)}>Cancel</button>
                       
                       
                   
                </div>
            </>
        )
    }

}
export default DeleteAllInnerComponent
