import React from 'react'
import Select from 'react-select'
import './Filter.css'

export default ({ onChange, items, value, className }) => {

    const options = []

    const getOptions = (items) => {
       
        if (options.length < 1) {
            for (let i = 0; i < items.length; i++) {
                console.log('item[i].value = ' + item[i].value);
                options.push(
                    { value: `${items[i].value || items[i].id}`, label: `${items[i].name}` })
            }

           
        }
        return options
    }
    
    const defaultValue = (options, value) => {
        console.log('default value value = ' + value);
        //console.log('found option = ' + options.find(option => option.value === value));
        options.map((o) => {
            console.log('o -> o.value = ' + o.value );
           // console.log('o -> o.value = ' + o.value + 'value = ' + value);
           // console.log('o.value == value = ' + (o.value == value));
           // console.log( 'o.value===value =' +( o.value === value));
        });
        return options ? options.find(option=>option.value === value) : ""
    }
    return (
        <Select
            className={className}
                options={getOptions(items)}
                value={defaultValue(options, value)}
                onChange={value => onChange(value)}
                placeholder={"..."}
                autosize={true}
                clearable={true}
                isSearchable={true}
            />
        )
}
