import React, { Component } from 'react';
//import DeliveryDetailDataService from '../service/DeliveryDetailDataService';
import DeliveryDataService from '../service/DeliveryDataService';
//import ProductDetailDataService from '../service/ProductDetailDataService';
import '../myStyles/Style.css';
//import ProductDetailDataService from '../service/ProductDetailDataService';

class DeliveryInnerComponent extends Component {
    constructor(props) {
        super(props)

        this.state =
            {
                deliveryUpdateShow: props.deliveryUpdateShow,
                items: props.items,
                message: props.message

            }
    }

    saveUpdated = () => {
        let items = this.state.items;
        let item = items[this.state.deliveryUpdateShow.x];
        this.setState({hello : "hello you"})



    }

    render() {
        return (
            <>
                <div className={this.state.deliveryUpdateShow.show ? "overlay d-block" : "d-none"}></div>
                <div className={this.state.deliveryUpdateShow.show ? "modal d-block" : "d-none"} style={{width:"80%"}}>
                    <span class="close" onClick={() => this.props.updateClickedInner(null)}>&times;</span>
                    <h2>update delivery </h2>
                    {this.state.deliveryUpdateShow.error && this.state.deliveryUpdateShow.error.length > 1 && <div className="alert alert-success d-flex">{this.state.deliveryUpdateShow.error}
                        <i class="fa fa-close ml-auto pr-3 pt-1"
                            onClick={() => {
                                let show = this.state.deliveryUpdateShow;
                                show.error = '';
                                this.setState({ deliveryUpdateShow: show })
                            }}>
                        </i>
                    </div>}
                    <h6 className={this.state.deliveryUpdateShow.error && this.state.deliveryUpdateShow.error.length > 1 ? "ml-5" : "mt-5 ml-5"}>number : </h6>
                    <input type="text" value={this.state.deliveryUpdateShow.delivery.number} read only />
                    <h6>supplier</h6>
                    <input value={this.state.hello} />
                    <h6>date</h6>

                    <button className="btn btn-mybtn px-5 mt-3 ml-5" onClick={this.saveUpdated}>Save</button>
                    <button className="btn btn-mybtn btn-delete px-5 mt-3 ml-3" onClick={() => this.props.updateClickedInner(null)}>Cancel</button>
                </div>
            </>
        )
    }
}

export default DeliveryInnerComponent
