import React, { Component } from 'react';
import { EventSourcePolyfill } from 'event-source-polyfill';

class EventListner extends Component {

    constructor(props) {
        super(props)
        // ... super, state, and columns ...

        this.eventSource = new EventSource('http://localhost:8080/api/inventory/manager/subscribe', { withCredentials: true });
    }

    componentDidMount() {
        this.eventSource.onmessage = e =>
            console.log("msg from sse = " + e.data);
    }
}

export default EventListner
