import React, { Component } from 'react';
import DeliveryDataService from '../service/DeliveryDataService';
import PaginationComponent from './PaginationComponent';
//import ProductFilter from './Filters/ProductFilter';
import '../myStyles/Style.css';
import { CSVLink } from "react-csv";

const headers = [
    { label: "number", key: "number" },
    { label: "date", key: "date" },
    { label: "supplier", key: "supplier.name" },
    { label: "product", key: "deliveryDetails.productName" },
    { label: "quantity", key: "deliveryDetails.quantity" },
    { label: "unit price", key: "deliveryDetails.pricePerOne" }
];

class ListDeliveriesComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [],
            message: null,
            pager: null,
            filter: null,
            search: window.location.search || '',
            alldata: [],
        }
        this.refresh = this.refresh.bind(this)
        this.deleteClicked = this.deleteClicked.bind(this)
        this.updateClicked = this.updateClicked.bind(this)
        this.addClicked = this.addClicked.bind(this)
        this.csvLink = React.createRef();
    }

    componentDidMount() {
        this.refresh();
    }

    refresh() {
       DeliveryDataService.retrieveAll(this.state.search)
            .then(
                response => {
                    this.setState({
                        items: response.data.items,
                        pager: response.data.pager,
                        filter: response.data.filter
                    });
                }
            )
    }

    downloadReport = () => {
        let newSearch = this.getSearchAll();
        DeliveryDataService.retrieveAll(newSearch)
            .then(response => {
                this.setState({ alldata: response.data.items });
                this.csvLink.current.link.click()
            })
    }

    getSearchAll = () => {
        let search = this.state.search;
        search.replace(/\s+/g, '');
        let newSearch = '';
        let searchItems = search.split('&');
        for (let i = 0; i < searchItems.length; i++) {
            if (searchItems[i].length < 1 || searchItems[i].startsWith('Pager.itemsPerPage'))
                continue
            else
                newSearch += searchItems[i] + '&'
        }
        newSearch = '?' + newSearch;
        if (newSearch.length > 1) newSearch += '&'
        newSearch += 'Pager.itemsPerPage=2147483647';
        return newSearch;
    }

    deleteClicked(id) {
        DeliveryDataService.delete(id)
            .then(
                response => {
                    this.setState({ message: `Delete successful` })
                    this.refresh()
                }
            )
    }

    updateClicked(id) {
        this.props.history.push(`/deliveries/${id}`)
    }

    addClicked() {
        this.props.history.push(`/deliveries/-1`)
    }

    togglemsgbox = () => {
        this.setState({ message: null })
    }

    render() {
        const data = this.state.items;
        const dataAll = '';
        return (
            <div className="px-3">
                {/*this.state.filter && <ProductFilter {...this.state.filter} />*/}
                <div className="border">
                    <div className="panel-heading">
                        <h6 className="panel-title p-2">
                            <strong> Deliveries</strong>
                        </h6>
                    </div>
                    <div className="p-1">
                        <div className=" pt-3 px-2 mx-3 d-flex flex-wrap">
                            <div>
                                <button className="btn btn-mybtn px-5  " onClick={this.addClicked}>Add New</button>
                                <CSVLink
                                    className="btn btn-mybtn px-3 ml-2"
                                    data={data} headers={headers} filename={"deliveries-page.csv"}
                                    asyncOnClick={true}
                                    onClick={() => {
                                        console.log("You click the link");
                                    }}
                                >
                                    Download this page
                                </CSVLink>
                                <button className="btn btn-mybtn px-3 ml-2" onClick={this.downloadReport}>Download All</button>
                                <CSVLink
                                    data={this.state.alldata}
                                    filename={"deliveries-all.csv"}
                                    className="hidden"
                                    headers={headers}
                                    ref={this.csvLink}
                                    target="_blank"
                                />
                            </div>
                            {this.state.pager && <PaginationComponent {...this.state.pager} />}
                        </div>
                        {this.state.message && <div className="alert alert-success d-flex">{this.state.message}<i class="fa fa-close ml-auto pr-3 pt-1" onClick={this.togglemsgbox}></i></div>}

                       
                        {
                            
                                    this.state.items.map(
                                        item =>
                                            <div class="panel-body">
                                                <table class="table border-bottom exportableTable">
                                                    <tbody>
                                                        <tr>
                                                            <th>Delivery Number : {item.number} </th>
                                                            <th>Delivery Date : {item.date}</th>
                                                            <th>Delivery Supplier : {item.supplier.name}</th>
                                                            <th style={{ width: '100px'}}>
                                                                <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteClicked(item.id)}>Delete</button>
                                                            </th>
                                                         </tr>
                                                         <tr>
                                                         <td colspan="4">
                                                            <table class="table table-bordered exportableTable">
                                                                <tbody>
                                                                    <tr>
                                                                        <td>Product</td>
                                                                        <td>Quantity</td>
                                                                        <td>Unit Price</td>
                                                                        <td>Total</td>
                                                                            <td style={{ width: '140px' }}></td>
                                                                    </tr>
                                                                        {
                                                                            item.deliveryDetails.map(
                                                                                dd =>
                                                                                    <tr key={dd.id}>
                                                                                        <td>{dd.productName}</td>
                                                                                        <td>{dd.quantity}</td>
                                                                                        <td>{dd.pricePerOne}</td>
                                                                                        <td>{dd.pricePerOne * dd.quantity}</td>
                                                                                        <td><button className="btn btn-mybtn mr-1" onClick={() => this.updateClicked(dd.id)}>Update</button>
                                                                                            <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteClicked(dd.id)}>Delete</button></td>
                                                                                    </tr>
                                                                                )
                                                           
                                                                         }
                                                                </tbody>
                                                                </table>
                                                                <span class="pull-right" style={{ marginRight : '100px' }}><strong>Total Bill :  </strong> {}</span>
                                                         </td>
                                                        </tr>
                                                    </tbody>
                                                </table>
                                            </div>
                                            
                                         )
                                   }
                           

                        {/*
                        <table className="table border-bottom my-table" style={{ width: '100%' }}>
                            <thead>
                                <tr>
                                    <th scope="col" className="ws">number</th>
                                    <th scope="col">date</th>
                                    <th scope="col" className="ws">supplier</th>
                                    <th scope="col" className="wm">product</th>
                                    <th scope="col" className="ws">quantity</th>
                                    <th scope="col" className="ws">unit price</th>
                                    <th scope="col"></th>
                                </tr>
                            </thead>
                           
                            <tbody>
                                {
                                    this.state.items.map(
                                        item =>
                                            item.deliveryDetails.map(
                                                dd =>

                                                <tr scope="row" key={dd.id}>
                                                            <td >{item.number}</td>
                                                            <td >{item.date}</td>
                                                            <td >{item.supplier.name}</td>
                                                    <td >{dd.productName}</td>
                                                    <td >{dd.quantity}</td>
                                                    <td >{dd.pricePerOne}</td>
                                                    <td><button className="btn btn-mybtn mr-1" onClick={() => this.updateClicked(dd.id)}>Update</button>
                                                        <button className="btn btn-mybtn btn-delete" onClick={() => this.deleteClicked(dd.id)}>Delete</button></td>
                                                    </tr>
                                                )
                                                
                                            
                                    )
                                }
                            </tbody>
                            
                        </table>*/
                        }
                    </div>
                </div>
            </div>
        )
    }
}

export default ListDeliveriesComponent
