import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import ProductDataService from '../service/ProductDataService';
import '../myStyles/Style.css';
import CustomSelect from './Filters/CustomSelect';

class ProductComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            name: '',
            description: '',
            productType: '',
            productTypes: [],           
            subCategory: '',          
            subCategories: [],
            filteredSubCategories:[],
            categories: [],           
            amortizationPercent: '',
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
        this.cancelForm = this.cancelForm.bind(this)
    }

    componentDidMount() {
       
        ProductDataService.retrieve(this.state.id)
            .then(response => {
                console.log('got response in component did mount');
                if (this.state.id > 0) {
                    this.setState({
                        name: response.data.name,
                        description: response.data.description,
                        productType: response.data.productType,
                        subCategory: response.data.subCategory,
                        amortizationPercent: response.data.amortizationPercent,
                    })
                }
                this.setState({
                    productTypes: response.data.productTypes,
                    subCategories: response.data.subCategories,
                    filteredSubCategories: response.data.subCategories,
                    categories: response.data.categories,
                });
            })
    }

    onSubmit(values) {
        let item = {
            id: this.state.id,
            name: values.name,
           
            targetDate: values.targetDate
        }
        console.log('item = ' + item);
       ProductDataService.save(item)
            .then(() => this.props.history.push('/products'))
    }

    validate(values) {
        let errors = {}
        if (!values.name) {
            errors.name = 'required field !!!'
        } else if (values.name.length < 5) {
            errors.userName = 'Enter atleast 5 Characters'
        }

       

        return errors
    }

    cancelForm() {
        this.props.history.push('/products')
    }

    handleCheckedElement = (event) => {
        console.log('in handle checked element');
        if (event.target.value === this.state.productType) {           
        } else {
            this.setState({
                productType: event.target.value
            })
        }
    }

    filterSubCategories = (value) => {

        console.log('in filtersub categories value = ' + value.value);

        let filteredsubs = [];
        let subs = this.state.subCategories;
        for (let i = 0; i < subs.length; i++) {
            console.log('value.value = ' + value.value);
            console.log('subs[i].category.id = ' + subs[i].category.id);
            if (subs[i].category.id == value.value) {
                filteredsubs.push(subs[i])
            }
        }

        this.setState({
            filteredSubCategories: filteredsubs,
            subCategory:''
        })
    }

    setCategory = (value) => {

        let sub = this.state.subCategories.find(s => s.id == value.value);
        this.setState({
            subCategory: sub
        })


    }

    render() {
        console.log('rendering');
        let { id, name, description, productType, productTypes, amortizationPercent, categories, subCategory, subCategories, filteredSubCategories } = this.state
        return (
            <div className="container">
                {this.state.id > 0 ? <h3 className="mb-3"> Update Product</h3> : <h3 className="mb-3"> Add New Product </h3>}
                <Formik
                    initialValues={{ id, name, description, productType, productTypes, amortizationPercent, subCategory, categories, subCategories, filteredSubCategories}}
                    onSubmit={this.onSubmit}
                    validateOnChange={false}
                    validateOnBlur={false}
                    validate={this.validate}
                    enableReinitialize={true}
                >
                    {
                        (props, setFieldValue) => (
                            <Form>
                                <Field className="form-control" type="text" name="id" hidden />
                                <fieldset className="form-group">
                                    <label>name</label>
                                    <Field className="form-control w-25" type="text" name="name" />
                                    <ErrorMessage name="name" component="div"
                                        className="alert alert-warning" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>description</label>
                                    <Field className="form-control " as="textarea" type="textarea" name="description" />
                                    <ErrorMessage name="description" component="div"
                                        className="alert alert-warning" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>product type</label>
                                    {
                                        productTypes.map((type) =>
                                            <label className="mx-3">
                                                <Field
                                                    className="mx-1"
                                                    type="checkbox" name="productType" value={type.value}
                                                    checked={type.name === this.state.productType}
                                                    onClick={this.handleCheckedElement}/>
                                                {type.name}
                                    </label>)
                                    }
                                   
                                    <ErrorMessage name="productType" component="div"
                                        className="alert alert-warning" />
                                </fieldset>
                                {
                                    this.state.productType === 'DMA' &&
                                    <div>
                                    <fieldset className="form-group">
                                        <label>category</label>
                                        <CustomSelect                                               
                                                className={"w-50"}
                                                items={categories}
                                                value={subCategory !== '' ? subCategory.category.id : ''}
                                                onChange={this.filterSubCategories}
                                      
                                        />
                                        <ErrorMessage name=" selectedCategoryId" component="div"
                                            className="alert alert-warning" />
                                        </fieldset>
                                  
                               


                                    <fieldset className="form-group">
                                        <label>sub category</label>
                                        <CustomSelect
                                            name="subCategory"
                                            className={"w-50"}
                                                items={filteredSubCategories}
                                            value={subCategory !== '' ? subCategory.id : ''}
                                                onChange={this.setCategory}
                                        />
                                        <ErrorMessage name=" selectedCategoryId" component="div"
                                            className="alert alert-warning" />
                                        </fieldset>
                                    </div>
                                }
                                    <fieldset className="form-group">
                                        <label>amortization percent</label>
                                    <Field className="form-control w-50" type="number" name="amortizationPercent" />
                                    <ErrorMessage name="amortizationPercent" component="div"
                                            className="alert alert-warning" />
                                    </fieldset>
                                
                                <button className="btn btn-mybtn px-5" type="submit">Save</button>
                                <button className="btn btn-mybtn btn-delete px-5 ml-5" type="button" onClick={this.cancelForm}>cancel</button>
                            </Form>
                        )
                    }
                </Formik>
            </div>
        )
    }
}

export default ProductComponent
