import React, { Component } from 'react';
import UserDataService from '../service/UserDataService';

class ListUsersComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [],
            message: null,
            pager:null
        }
        this.refresh = this.refresh.bind(this)
        this.deleteClicked = this.deleteClicked.bind(this)
        this.updateClicked = this.updateClicked.bind(this)
        this.addClicked = this.addClicked.bind(this)
    }

    componentDidMount() {
        this.refresh();
    }

    refresh() {
       UserDataService.retrieveAll()
            .then(
                response => {
                    console.log(response);
                    this.setState({
                        items: response.data.items,
                        pager: response.data.pager
                    })
                    
                }
            )
    }

    deleteClicked(id) {
       UserDataService.delete(id)
            .then(
                response => {
                    this.setState({ message: `Delete successful` })
                    this.refresh()
                }
            )

    }

    updateClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/users/${id}`)
    }

    addClicked() {
        this.props.history.push(`/users/-1`)
    }

    render() {
        return (

            { PaginationComponent },

            <div className="container">
                <h3>All Users</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}

                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>first name</th>
                                <th>last name</th>
                                <th>user name</th>
                                <th>email</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.items.map(
                                    item =>
                                        <tr key={item.id}>
                                            <td>{item.firstName}</td>
                                            <td>{item.lastName}</td>
                                            <td>{item.userName}</td>
                                            <td>{item.email}</td>
                                            <td><button className="btn btn-success" onClick={() => this.updateClicked(item.id)}>Update</button></td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteClicked(item.id)}>Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>

                    </table>
                </div>
                <div className="row">
                    <button className="btn btn-success" onClick={this.addClicked}>Add</button>
                </div>
            </div>
        )
    }
}

export default ListUsersComponent
