import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
//import { useFormik } from 'formik'
import CustomSelect from './CustomSelect';


class UserFilter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            all:props.all,
            firstNames: props.firstNames,
            firstName: props.firstName,
            lastNames: props.lastNames,
            lastName: props.lastName,
            userNames: props.userNames,
            userName: props.userName,
            emails: props.emails,
            email: props.email,
            prefix: props.prefix
        }

        this.onSubmit = this.onSubmit.bind(this)
       // this.validate = this.validate.bind(this)

    }
    

    onSubmit(values) {

       /* let user = {
            id: this.state.id,
            firstName: values.firstName,
            lastName: values.lastName,
            userName: values.userName,
            email: values.email,
            targetDate: values.targetDate
        }*/

        let path = window.location.pathname;
        let search = window.location.search;
        let newPath = ``;

        if (search.length > 1) {
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            let searchItems = search.split('&');
            for (let i = 0; i < searchItems.length; i++) {

                if (searchItems[i].startsWith('Pager.itemsPerPage='))
                    newPath += searchItems[i] + '&'               

            }
           // newPath = path + '?' + newPath + this.props.prefix + '.itemsPerPage=' + selectedOption.value;
        }

        let prefix = this.state.prefix;
       /* if (values.firstName)
            newPath += prefix + '.' + values.firstName

        if (values.userName)
            newPath += prefix + '.userName=' + values.userName

        newPath = path + '?' + newPath ;
        console.log('new path =' + newPath);*/

       // let newpath2 = ``;
        Object.entries(values).map(([key, value]) => {
            if (!key.endsWith("s") && value) {
                newPath += prefix + '.' + key +'='+ value+'&'
            }
           
            // Pretty straightforward - use key for the key and value for the value.
            // Just to clarify: unlike object destructuring, the parameter names don't matter here.
        })
        newPath = newPath.substring(0, newPath.length-1);
        newPath = path + '?' + newPath;
        console.log('newPath =' + newPath);

       // window.location.href = newPath;


    }

   /* const formik = useFormik({
        // let { all, firstNames, firstName, lastNames, lastName, userNames, userName, emails, email } = this.state;
         initialValues: {
             email: '',
             job: ''
         },
        initialValues={ { all, firstNames, firstName, lastNames, lastName, userNames, userName, emails, email }= this.state },
        validate,
        onSubmit: values => {
            console.log(values)
        }

    })*/
   

    render() {
       /* const customControlStyles = base => ({
            width: 100,
        });*/

      /*  const styles = {
            container: base => ({
              ...base,
                flex: 1
            })
        };*/


        let { all, firstNames, firstName, lastNames, lastName, userNames, userName, emails, email } = this.state

        return (
                        
                <div className="container">
                    <Formik
                        initialValues={{ all, firstNames, firstName, lastNames, lastName, userNames, userName, emails, email }}
                        onSubmit={this.onSubmit}                       
                    enableReinitialize={true}
                   // setFieldValue
                    >
                    {
                        ({ props, setFieldValue }) => (
                                <Form>
                                    <fieldset className="border">

                                    <div className="row pt-3 pb-3">
                                        <div className="col-12">
                                            <div className="form-inline">

                                    <div className="form-group col-4">                                   
                                                    <label className="pr-3">first name :</label>
                                                    <div className="flex-grow-1">
                                        <CustomSelect   
                                           // styles={styles}
                                        items={firstNames}
                                        value={firstName}
                                                                onChange={value =>  this.setState({ firstName: value.value })}//firstName = value.value}
                                                        />
                                                    </div>
                                    </div>
                                   

                                        <div className="form-group col-4">   
                                                    <label className="pr-3">user name :</label>
                                                    <div className="flex-grow-1">
                                            <CustomSelect  
                                               // styles={styles}
                                                                items={userNames}
                                                            value={userName}
                                                            onChange={(selected) => 
                                                               

                                                                setFieldValue("userName", selected.value)
                                                           
                                                            }
                                                               // onChange={option => setFieldValue("userName", option)}
                                                               // onChange={value => initialValues.userName = value.value}
                                                                //setFieldValue('userName')}// this.setState({ firstName: value.value })}// userName = value.value} //
                                                        />
                                                    </div>
                                        </div>

                                       
                                        <div className="form-group col-4">  
                                            <label className="pr-3">email :</label>
                                                    <div className="flex-grow-1">
                                                <CustomSelect
                                                  //  styles={styles}
                                                items={emails}
                                                value={email}
                                                onChange={value => email = value.value} //this.setState({ firstName: value.value })}
                                                        />
                                                    </div>
                                        </div>

                                            </div>
                                        </div>
                                    </div>
                                          
                                        
                                        

                                        <button className="btn btn-success px-5 m-3" type="submit">Search</button>
                                    </fieldset>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
           
        )
    }
}

export default UserFilter
