import React, { Component } from 'react';
import ProductDetailDataService from '../service/ProductDetailDataService';
import UserProfileDataService from '../service/UserProfileDataService'
import '../myStyles/Style.css';
import DatePicker from "react-datepicker";
import CustomSelect from './Filters/CustomSelect';
import ProductDetailFilter from './Filters/ProductDetailFilter';


class UserProfileInnerComponent extends Component {
    constructor(props) {
        super(props)

        this.state =
            {
                profileShow: props.profileShow,
                items: props.items,
                message: props.message,
                filter: props.filter,
            filteredNumbers: [],
            lastSearch: '?isDiscarded=false&isAvailable=true&freeInventory=true',
            selectedDate: null,
            filteredcount: '',
            selectedPds:[]

            }
        this.refresh = this.refresh.bind(this)
    }

    componentDidMount() {

        console.log("user profile inner component did mount ")
       // let search = '?isDiscarded=false&isAvailable=true&freeInventory=true';
        this.refresh(this.state.lastSearch)
       
    }

    refresh(search) {

        ProductDetailDataService.retrieveAllNumbers(search)
            .then(response => {
                console.log("got response");
                this.setState({
                    filteredNumbers: response.data,
                    filteredcount: response.data.length
                })
                console.log("data length = " + this.state.filteredNumbers.length);
            }).catch(error =>
                console.log("error = " + error)
                )
    }

    saveUpdated = () => {
      /*  let x = this.state.deliveryUpdateShow.x;
        let items = this.state.items;
        let item = items[x];

        let show = this.state.deliveryUpdateShow;
        let supplierId = show.delivery.supplierId;
        let date = show.delivery.date;

        // console.log("date.toISOString() = " + date.toISOString());
        // console.log("date = " + date);
        // console.log("new Date(item.date) = " + new Date(item.date));
        //console.log("new Date(item.date) === date " +(new Date(item.date) === date ));

        if (!date || !supplierId) {
            show.error = "required fields can't be empty !!!";
            this.setState({ deliveryUpdateShow: show })
        } else if (supplierId == item.supplierId && this.isDateEqual(date, item.date)) {
            show.error = "delivery hasn't changed";
            this.setState({ deliveryUpdateShow: show })
        } else {
            console.log("all good");
            let item = show.delivery;
            //console.log("in submit delivery item = " + JSON.stringify(item));
            DeliveryDataService.save(item)
                .then((response) => {
                    console.log("delivery submit response = " + response.data);
                    //item.supplierName = (this.state.suppliers.filter(s => s.value == item.supplierId))[0].name;
                    items[x] = item;
                    //message = "update successful"
                    this.props.setItems(items);
                    this.props.updateClickedInner(null);
                    let msg = this.state.message;
                    msg = "update successful";
                    this.props.setMessage(msg);
                    //this.props.setdeliveryUpdateShow(null)


                    //let path = this.state.deliveryView.length > 0 ? '/deliveries?deliveryView=DeliveryDetailView' : '/deliveries';
                    // this.props.history.push(path)
                }).catch((error) => {
                    console.log("delivery submit error= " + error.data);
                    show.error = "errors occured : " + error + " !!!";
                    this.setState({ deliveryUpdateShow: show })
                })
        }*/

    }

    onProductChange = (selected) => {

        console.log("selected.value = " + selected.value);
        this.getNewSearch('productId', selected.value);
       // let search = this.getNewSearch('productId', selected.value);
        //this.refresh(search)
        


    }
    getNewSearch = (name, value) => {

        let search = this.state.lastSearch;
        let newPath = ``;

        if (search.length > 1) {
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            let searchItems = search.split('&');
            for (let i = 0; i < searchItems.length; i++) {
                if (!searchItems[i].startsWith(name))
                    newPath += searchItems[i] + '&'               
            }
        }

      
        if (value && value != 'undefined')
            newPath += name + '=' + value;
        else {
            if (name == 'notIn') {
                let ids = ''
                this.state.selectedPds.map(pd => ids += pd.value + ',')
                ids = ids.substring(0, newPath.length - 1);
                newPath+= name+'['+ids+']' 
            }
            else
                newPath = newPath.substring(0, newPath.length - 1);
        }
        console.log('newPath =' + newPath);
         newPath = '?' + newPath
        this.setState({ lastSearch: newPath })
        this.refresh(newPath)
       // return newPath;


    }
    oninventoryAdd = (selected) => {
        if (selected.name == '') return;
        let selectedPds = this.state.selectedPds;
        selectedPds.push({ name: selected.label, value: selected.value })
        this.setState({ selectedPds: selectedPds })
        this.getNewSearch('notIn')

    }
    oninventoryRemove = (i) => {
       
        let selectedPds = this.state.selectedPds;
        selectedPds.splice(i, 1);
        this.setState({ selectedPds: selectedPds })
        this.getNewSearch('notIn')
    }


    onDateChange = (date) => {
        console.log("2 = " + date);
        if (date != null) console.log("type of 2" + typeof (date));
        console.log("this.state.selectedDate 1 = " + this.state.selectedDate);
        if (this.state.selectedDate != null) console.log("type of 1" + typeof(this.state.selectedDate));
        

        if (this.isDateEqual(this.state.selectedDate, date)) return;
        
       if(date) {
            date = date.toISOString();
            date = date.substring(0, date.indexOf('T'))
        } 
        /*date = new Intl.DateTimeFormat("en-GB", {
            month: "numeric",
            day: "2-digit",
            year: "numeric",
        }).format(new Date(date));*/
        console.log("date = " + date);
        this.setState({ selectedDate: date });
        this.getNewSearch('dateCreatedAfter', date);
       // let search = 
        
        
    }

    isDateEqual = (date1, date2) => {
        //date2 string
        // date1 string or date 
        console.log("typeof date1 = " + typeof date1);
        console.log("typeof date2 = " + typeof date2);
        console.log("date1 = " + date1);
        console.log(" date2 = " + date2);
        if (date1 == date2) return true       
        else {
            date1 = new Date(date1);
            date2 = new Date(date2);
            if (date1.getFullYear() === date2.getFullYear() &&
                date1.getMonth() === date2.getMonth() &&
                date1.getDate() === date2.getDate())
                return true
        }
        return false
    }
    

    render() {
        return (
            <>
                <div className={this.state.profileShow.show ? "overlay d-block" : "d-none"}></div>
                <div className={this.state.profileShow.show ? "modal d-block " : "d-none"} style={{ width: "80%", height: "60%", overflow: "visible" }}>


                    <span class="close pt-3" onClick={() => this.props.updateClicked(null)}>&times;</span>
                    <h2>{this.state.profileShow.up ? 'Update' : 'Add'} profile &emsp;({this.state.profileShow.profile.userName})</h2>
                   
                    <div className="mt-3 inline w100">
                        <div className="border pt-1 b-r r-c foo"> {/*********************** small filter **************************/}
                            <label className="move-top top-l">filter for inventory select</label>

                            <div className="inline w40 pr-1">
                                <h6>product : </h6>
                                <CustomSelect
                                    className={"inline w100 ml-0"}
                                    items={this.state.filter.productNames}
                                    onChange={(selected) => this.onProductChange(selected)}
                                />
                            </div>

                            <div className="inline w20">
                                <h6 className="">created after :</h6>
                                <div className="inline w100">
                                    <DatePicker
                                        className="form-control w100 p-2 ml-0"
                                        dateFormat="dd MMMM yyyy"
                                        locale="en-GB"
                                        isClearable
                                        placeholderText="..."
                                        selected={this.state.selectedDate && new Date(this.state.selectedDate) || null}
                                        onChange={selected => {
                                            this.onDateChange(selected)
                                        }} />
                                </div>
                            </div>

                            <span className="move-top top-r"> {this.state.filteredcount} items found</span>
                            <div className="inline w40">
                                <h6 className="inline">inventory :&nbsp;</h6>
                                <CustomSelect
                                    className={"inline w100 ml-0"}
                                    items={this.state.filteredNumbers}
                                    onChange={(selected) => this.oninventoryAdd(selected)}
                                />
                            </div>

                        </div>
                    </div>{/*******************    filter over  ******************/}

                    {this.state.profileShow.error && this.state.profileShow.error.length > 1 && // error div
                        <div className="alert alert-warning d-flex">{this.state.profileShow.error}
                            <i class="fa fa-close ml-auto pr-3 pt-1"
                                onClick={() => {
                                    let show = this.state.profileShow;
                                    show.error = '';
                                    this.setState({ profileShow: show })
                                }}>
                            </i>
                        </div>
                    }
                    {/***************  form  ********************/}
                    <div className={this.state.profileShow.error && this.state.profileShow.error.length > 1 ?
                        "d-flex align-items-top flex-wrap" : "mt-5 d-flex align-items-top flex-wrap"}>

                        <div className="inline w50 m-0 pl-5">{/*************** form right **************/}

                            <div className="inline m-0 pl-5">
                    <h6 >date given :</h6> 
                    <div>
                        <DatePicker
                                        className="form-control inline-2 p-2"
                            dateFormat="dd MMMM yyyy"
                            locale="en-GB"     
                            /*isClearable
                            placeholderText="select Date!"*/
                            selected={(this.state.profileShow.profile.givenAt && new Date(this.state.profileShow.profile.givenAt))|| new Date()}
                            onChange={date => {
                                this.onDateChange(date);
                                //console.log("date changed = " + date);                               
                            }} />
                    </div>
                    </div>

                            <div className="inline m-0 px-5">
                        <h6 >date returned :</h6>
                        <div>
                            <DatePicker
                                        className="form-control inline-2 p-2"
                                dateFormat="dd MMMM yyyy"
                                locale="en-GB"
                                disabled
                                /*isClearable
                                placeholderText="select Date!"*/
                                selected={(this.state.profileShow.profile.returnedAt && new Date(this.state.profileShow.profile.returnedAt)) || null}
                                onChange={date => {
                                    this.onDateChange(date);
                                    //console.log("date changed = " + date);                               
                                }} />
                        </div>
                            </div>

                            <div className="mt-3 mb-5 d-flex justify-content-center">
                                <button className="btn btn-mybtn px-5" onClick={this.saveUpdated}>Save</button>
                                <button className="btn btn-mybtn btn-delete px-5" onClick={() => this.props.updateClicked(null)}>Cancel</button>
                            </div>
                        
                        </div> {/*************** form right over  **************/}

                        <div className="inline w50 mb-5">{/*************** form left **************/}
                           
                            <h6 className="">selected inventory :</h6>
                            {this.state.selectedPds &&
                                this.state.selectedPds.map((pd, i) =>
                                    <div>                                        
                                    {i+1} :&nbsp;<input value={pd.name} className='form-control inline w80 m-0 p-2 pl-3' />
                                       
                                        <button className="btn btn-mybtn btn-delete m-0 ml-1" type="button"
                                            onClick={() => {
                                                this.oninventoryRemove(i);
                                            }}><i class="fa fa-close ml-auto"
                                                onClick={() => {
                                                    this.oninventoryRemove(i);
                                                }}>
                                            </i></button>
                                    </div>
                                )}                            
                        </div>
                    </div>
                    </div>
            </>
        )
    }
}

export default UserProfileInnerComponent
