import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import CustomSelect from './CustomSelect';
import './Filter.css'

class ProductFilter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            all: props.all,
            name: props.name,
            names: props.names,
            productType: props.productType,
            productTypes: props.productTypes,
            amortizationPercentMoreThan: props.amortizationPercentMoreThan,
            amortizationPercentLessThan: props.amortizationPercentLessThan,
            totalCountMoreThan: props.totalCountMoreThan,
            totalCountLessThan: props.totalCountLessThan,
            prefix: props.prefix,
            minmore : 0,
            maxmore : 100,
            minless : 0,
            maxless : 100,
        }

        this.onSubmit = this.onSubmit.bind(this)
        this.resetForm = this.resetForm.bind(this)
    }

    onSubmit(values) {

        let path = window.location.pathname;
        let search = window.location.search;
        let newPath = ``;

        if (search.length > 1) {
            while (search.charAt(0) === '?') {
                search = search.substring(1);
            }
            let searchItems = search.split('&');
            for (let i = 0; i < searchItems.length; i++) {

                if (searchItems[i].startsWith('Pager.itemsPerPage='))
                    newPath += searchItems[i] + '&'
            }
        }

        let prefix = this.state.prefix;
        Object.entries(values).map(([key, value]) => {
            console.log('field key =' + key);
            console.log('field value =' + value);
            if (!key.endsWith("s") && value) {
                if ((values.productType === 'MA' && key === 'amortizationPercentMoreThan') ||
                    (values.productType === 'MA' && key === 'amortizationPercentLessThan') ||
                    (key === 'maxmore')) { }
                else { newPath += prefix + '.' + key + '=' + value + '&' }
            }

        })
        newPath = newPath.substring(0, newPath.length - 1);
        newPath = path + '?' + newPath;
        console.log('newPath =' + newPath);

        window.location.href = newPath;
    }

    resetForm() {

        window.location.href = window.location.pathname;
       // values.name = null;

       // this.props.history.push('/products');
       /* this.setState({
            all: '',
            name: '',
            productType: '',
            amortizationPercentMoreThan: '',
            amortizationPercentLessThan: '',
            totalCountMoreThan: '',
            totalCountLessThan:''
        });
        console.log('in reset form ');*/
    }


    handleCheckedElement = (event) => {
        if (event.target.value === this.state.productType) {
            this.setState({
                productType: ''
            })
        } else {
            this.setState({
                productType: event.target.value
            })
        }
    }

    changelessValues = (value) => {
       
            this.setState({
            minless: value.target.value,
            amortizationPercentMoreThan: value.target.value
        })
    }

    changemoreValues = (value) => {
        console.log('change more value');
        console.log('this.state.maxmore value = ' + this.state.maxmore);
        console.log('value.target.value = ' + value.target.value);
        this.setState({
            maxmore: value.target.value,
            amortizationPercentLessThan: value.target.value
        });
        console.log('this.state.maxmore value = ' + this.state.maxmore);
        console.log('this.state.amortizationPercentLessThan = ' + this.state.amortizationPercentLessThan);
    }

    render() {

        let { all, name, names, productType, productTypes, amortizationPercentMoreThan, amortizationPercentLessThan,
            totalCountMoreThan, totalCountLessThan, minmore, minless, maxmore, maxless } = this.state

        return (

            <Formik
                initialValues={{
                    all, name, names, productType, productTypes, amortizationPercentMoreThan,
                    amortizationPercentLessThan, totalCountMoreThan, totalCountLessThan, minmore, minless, maxmore, maxless
                }}
                onSubmit={this.onSubmit}
                enableReinitialize={true}
            >
                {({ props, setFieldValue }) => (
                    <Form className="filter-form">
                        <fieldset >
                            <div className="inline">
                                <label>name&nbsp;</label>
                                <CustomSelect                                    
                                    className={"inline inline-1-5"}
                                    items={names}
                                    value={name}
                                    onChange={(selected) => setFieldValue("name", selected.value)}
                                />
                            </div>
                            {
                                /* 
                                 <div className="inline px-2 border">
                                <label className="mb-1">total :&nbsp;</label>
                                <label className="mb-1">more than&nbsp;</label>
                                <Field className="form-control in-inline inline-50px" type="number" min="0" name="totalCountMoreThan" />
                                <label className="pl-1 mb-1">less than&nbsp;</label>
                                <Field className="form-control in-inline inline-50px" type="number" min="0" name="totalCountLessThan" />
                                </div>
                                */
                            }
                            <div className="inline px-2 mx-2">
                                <label>product type :</label>
                                {                                     
                                    productTypes.map((type) =>
                                        <div className="inline">                                        
                                            <Field 
                                                className="mx-2" type="checkbox" name="productType"
                                                value={type.value} checked={type.name === this.state.productType}
                                                onClick={this.handleCheckedElement} />
                                            {type.name}
                                        </div>
                                   )
                                }
                            </div>
                            {
                                <div className={this.state.productType === 'MA' ? "inline px-2 d-none border" : "inline px-2 border"}>
                                    <label className="mb-1">amortization :&nbsp;</label>
                                    <label className="mb-1">more than&nbsp;</label>
                                    <Field className="form-control in-inline inline-50px" type="number" min={minmore} max={maxmore}
                                        name="amortizationPercentMoreThan" onChange={this.changelessValues} />%
                                    <label className="pl-2 mb1">less than&nbsp;</label>
                                    <Field className="form-control in-inline inline-50px" type="number" min={minless} max={maxless}
                                        name="amortizationPercentLessThan" onChange={this.changemoreValues}/>%
                            </div>
                            }
                            <div className="inline">
                                <button className="button" type="submit">Search</button>
                                <button className="button btn-delete" type="reset" onClick={this.resetForm}>reset</button>
                            </div>
                        </fieldset>
                    </Form>
                )
                }
            </Formik>
        )
    }
}

export default ProductFilter
